angular.module("$strap.directives").directive("bsNavbar",function(t){return{restrict:"A",link:function(e,a,n,i){e.$watch(function(){return t.path()},function(t,e){$("li[data-match-route]",a).each(function(e,a){var n=angular.element(a),i=n.attr("data-match-route"),r=new RegExp("^"+i+"$",["i"]);if(r.test(t)){n.addClass("active");var c=n.find(".collapse.in");c.length&&c.collapse("hide")}else n.removeClass("active")})})}}});