describe("uiAnimate",function(){var i,e,a,t=angular.module("ui.config");beforeEach(module("ui.directives")),beforeEach(inject(function(t,s,u){i=t,e=s,a=u})),afterEach(function(){t.value("ui.config",{})}),describe("behavior",function(){it("should add a ui-animate class when the dom is compiled",function(){var a=e("<div ui-animate></div>")(i);expect(a.hasClass("ui-animate")).toBeTruthy()}),it("should remove the ui-animate class immediately after injection",function(){var t=e("<div ui-animate></div>")(i);a.flush(),expect(t.hasClass("ui-animate")).toBeFalsy()})}),describe("options",function(){describe("passed",function(){it("should use a string as the class",function(){var a=e("<div ui-animate=\" 'ui-hide' \"></div>")(i);expect(a.hasClass("ui-hide")).toBeTruthy()}),it("should use an object's class attribute as the class",function(){var a=e("<div ui-animate=\" { 'class' : 'ui-hide' } \"></div>")(i);expect(a.hasClass("ui-hide")).toBeTruthy()})}),describe("global",function(){var a;beforeEach(inject(function(i){a=i.get("ui.config")})),it("should use a string as the class",function(){a.animate="ui-hide-global";var t=e("<div ui-animate></div>")(i);expect(t.hasClass("ui-hide-global")).toBeTruthy()}),it("should use an object's class attribute as the class",function(){a.animate={"class":"ui-hide-global"};var t=e("<div ui-animate></div>")(i);expect(t.hasClass("ui-hide-global")).toBeTruthy()})})})});