describe("uiReset",function(){"use strict";var e,t;beforeEach(module("ui.directives")),beforeEach(inject(function(o,i,n){e=o.$new(),t=i})),describe("compiling this directive",function(){it("should throw an error if we have no model defined",function(){function o(){t('<input type="text" ui-reset/>')(e)}expect(o).toThrow()}),it("should proper DOM structure",function(){e.foo="bar",e.$digest();var o=t('<input type="text" ui-reset ng-model="foo"/>')(e);expect(o.parent().is("span")).toBe(!0),expect(o.next().is("a")).toBe(!0)})}),describe("clicking on the created anchor tag",function(){it("should prevent the default action",function(){var o=t('<input type="text" ui-reset ng-model="foo"/>')(e);spyOn($.Event.prototype,"preventDefault"),o.siblings().get(0).click(),expect($.Event.prototype.preventDefault).toHaveBeenCalled()}),it("should set the model value to null and clear control when no options given",function(){e.foo="bar";var o=t('<input type="text" ui-reset ng-model="foo"/>')(e);e.$digest(),expect(o.val()).toBe("bar"),o.next().click(),expect(e.foo).toBe(null),expect(o.val()).toBe("")}),it("should set the model value to the options scope variable when a string is passed in options",function(){e.foo="bar",e.resetTo="i was reset";var o=t('<input type="text" ui-reset="resetTo" ng-model="foo"/>')(e);e.$digest(),expect(o.val()).toBe("bar"),o.next().click(),expect(e.foo).toBe("i was reset"),expect(o.val()).toBe("i was reset")})})});