describe("uiScrollfix",function(){"use strict";var i,s,e;beforeEach(module("ui.directives")),beforeEach(inject(function(l,o,c){i=l.$new(),s=o,e=c})),describe("compiling this directive",function(){it('should bind to window "scroll" event with a ui-scrollfix namespace',function(){spyOn($.fn,"on"),s('<div ui-scrollfix="100"></div>')(i),expect($.fn.on).toHaveBeenCalled(),expect($.fn.on.mostRecentCall.args[0]).toBe("scroll.ui-scrollfix")})}),describe("scrolling the window",function(){it("should add the ui-scrollfix class if the offset is greater than specified",function(){var l=s('<div ui-scrollfix="-100"></div>')(i);$(e).trigger("scroll.ui-scrollfix"),expect(l.hasClass("ui-scrollfix")).toBe(!0)}),it("should remove the ui-scrollfix class if the offset is less than specified (using absolute coord)",function(){var l=s('<div ui-scrollfix="100" class="ui-scrollfix"></div>')(i);$(e).trigger("scroll.ui-scrollfix"),expect(l.hasClass("ui-scrollfix")).toBe(!1)}),it("should remove the ui-scrollfix class if the offset is less than specified (using relative coord)",function(){var l=s('<div ui-scrollfix="+100" class="ui-scrollfix"></div>')(i);$(e).trigger("scroll.ui-scrollfix"),expect(l.hasClass("ui-scrollfix")).toBe(!1)})})});