angular.module("ui.directives").directive("uiShow",[function(){return function(i,u,e){i.$watch(e.uiShow,function(i,e){i?u.addClass("ui-show"):u.removeClass("ui-show")})}}]).directive("uiHide",[function(){return function(i,u,e){i.$watch(e.uiHide,function(i,e){i?u.addClass("ui-hide"):u.removeClass("ui-hide")})}}]).directive("uiToggle",[function(){return function(i,u,e){i.$watch(e.uiToggle,function(i,e){i?u.removeClass("ui-hide").addClass("ui-show"):u.removeClass("ui-show").addClass("ui-hide")})}}]);