describe("uiSortable",function(){beforeEach(module("ui.directives")),describe("simple use",function(){it("should have a ui-sortable class",function(){inject(function(e,i){var t;t=e("<ul ui-sortable></ul>")(i),expect(t.hasClass("ui-sortable")).toBeTruthy()})}),it("should update model when order changes",function(){inject(function(e,i){var t;t=e('<ul ui-sortable ng-model="items"><li ng-repeat="item in items" id="s-{{$index}}">{{ item }}</li></ul>')(i),i.$apply(function(){return i.items=["One","Two","Three"]}),t.find("li:eq(1)").insertAfter(t.find("li:eq(2)"))})})})});