describe("highlight",function(){var t,i="Prefix Highlight Suffix";beforeEach(module("ui.filters")),beforeEach(inject(function(i){t=i("highlight")})),describe("case insensitive",function(){it("should highlight a matching phrase",function(){expect(t(i,"highlight")).toEqual('Prefix <span class="ui-match">Highlight</span> Suffix')}),it("should highlight nothing if no match found",function(){expect(t(i,"no match")).toEqual(i)}),it("should highlight nothing for the undefined filter",function(){expect(t(i,void 0)).toEqual(i)}),it("should work correctly for number filters",function(){expect(t("3210123",0)).toEqual('321<span class="ui-match">0</span>123')}),it("should work correctly for number text",function(){expect(t(3210123,"0")).toEqual('321<span class="ui-match">0</span>123')})}),describe("case sensitive",function(){it("should highlight a matching phrase",function(){expect(t(i,"Highlight",!0)).toEqual('Prefix <span class="ui-match">Highlight</span> Suffix')}),it("should highlight nothing if no match found",function(){expect(t(i,"no match",!0)).toEqual(i)}),it("should highlight nothing for the undefined filter",function(){expect(t(i,void 0,!0)).toEqual(i)}),it("should work correctly for number filters",function(){expect(t("3210123",0,!0)).toEqual('321<span class="ui-match">0</span>123')}),it("should work correctly for number text",function(){expect(t(3210123,"0",!0)).toEqual('321<span class="ui-match">0</span>123')}),it("should not highlight a phrase with different letter-casing",function(){expect(t(i,"highlight",!0)).toEqual(i)})}),it("should highlight nothing if empty filter string passed - issue #114",function(){expect(t(i,"")).toEqual(i)})});