module("Calendar Weeks",{setup:function(){this.input=$('<input type="text">').appendTo("#qunit-fixture").val("2013-01-14").datepicker({format:"yyyy-mm-dd",calendarWeeks:!0}).focus(),this.dp=this.input.data("datepicker"),this.picker=this.dp.picker},teardown:function(){this.picker.remove()}}),test("adds cw header column",function(){var e=this.picker.find(".datepicker-days thead tr:nth-child(3) th:first-child");ok(e.hasClass("cw"),"First column heading is from cw column")}),test("adds calendar week cells to each day row",function(){var e=this.picker.find(".datepicker-days tbody tr");expect(e.length),e.each(function(e){var t=$(this).children().first();ok(t.hasClass("cw"),"First column is cw column")})}),test("displays correct calendar week",function(){var e=this.picker.find(".datepicker-days tbody tr");expect(e.length),e.each(function(e){var t=$(this).children().first();equal(t.text(),e+1,"Displays correct calendar weeks")})}),test("it prepends column to switcher thead row",function(){var e=this.picker.find(".datepicker-days thead tr:nth-child(2)");equal(e.children().length,3,"first row has 3 columns"),ok(!e.children().first().hasClass("cw"),"cw column is not prepended")});