describe("Slider with ticks tests",function(){var e;it("Should have the number of tick marks you specify",function(){e=$("#testSlider1").slider({ticks:[100,200,300,400,500]});var t=$("#testSlider1").siblings("div.slider").find(".slider-tick").length;expect(t).toBe(5)}),it("Should be at the default positions",function(){e=$("#testSlider1").slider({ticks:[100,200,300,400,500]}),$("#testSlider1").siblings("div.slider").find(".slider-tick").each(function(e){expect(this.style.left).toBe(100*e/4+"%")})}),it("Should be at the positions you specify",function(){var t=[0,10,20,30,100];e=$("#testSlider1").slider({ticks:[100,200,300,400,500],ticks_positions:t}),$("#testSlider1").siblings("div.slider").find(".slider-tick").each(function(e){expect(this.style.left).toBe(t[e]+"%")})}),it("Should have the tick labels you specify",function(){var t=["$0","$100","$200","$300","$400"];e=$("#testSlider1").slider({ticks:[100,200,300,400,500],ticks_labels:t});var i=$("#testSlider1").siblings("div.slider").find(".slider-tick-label");expect(i.length).toBe(t.length),i.each(function(e){expect(this.innerHTML).toBe(t[e])})}),it("Should overwrite the min/max values",function(){e=$("#testSlider1").slider({ticks:[100,200,300,400,500],min:15e3,max:25e3}),expect(e.slider("getAttribute","min")).toBe(100),expect(e.slider("getAttribute","max")).toBe(500)}),it("Should not snap to a tick within tick bounds when using the keyboard navigation",function(){e=$("#testSlider1").slider({ticks:[100,200,300,400,500],ticks_snap_bounds:30});var t=$("#testSlider1").siblings("div.slider:first").find(".slider-handle");t.focus();var i=document.createEvent("Events");i.initEvent("keydown",!0,!0);var s=0;t.on("keydown",function(){s++;var e=$("#testSlider1").slider("getValue");expect(e).toBe(100+s)}),i.keyCode=i.which=39;for(var n=0;5>n;n++)t[0].dispatchEvent(i)}),it("Should show the correct tick marks as 'in-selection', according to the `selection` property",function(){var t={ticks:[100,200,300,400,500],value:250,selection:"after"},i=$("#testSlider1");e=i.slider(t),expect(i.siblings("div.slider").find(".in-selection").length).toBe(3),e.slider("destroy"),t.selection="before",e=i.slider(t),expect(i.siblings("div.slider").find(".in-selection").length).toBe(2)}),afterEach(function(){e&&(e.slider("destroy"),e=null)})});