describe("'tooltip_split' Option tests",function(){var t,i,e,o,s,n="tooltipedSlider",l={id:n,value:[0,10],tooltip:"always"};describe("When 'tooltip_split' is false",function(){beforeEach(function(){t=$("#testSlider1").slider($.extend(l,{tooltip_split:!1})),i=$("#"+n),e=i.find(".tooltip-main"),o=i.find(".tooltip-min"),s=i.find(".tooltip-max")}),it("should have `tooltip-main` displayed with `in` class",function(){expect(e.css("display")).not.toBe("none"),expect(e.hasClass("in")).toBeTruthy()}),it("should have `tooltip-min, tooltip-max` not displayed",function(){expect(o.css("display")).toBe("none"),expect(o.hasClass("in")).toBeFalsy(),expect(s.css("display")).toBe("none"),expect(s.hasClass("in")).toBeFalsy()})}),describe("When 'tooltip_split' is true",function(){beforeEach(function(){t=$("#testSlider1").slider($.extend(l,{tooltip_split:!0})),i=$("#"+n),e=i.find(".tooltip-main"),o=i.find(".tooltip-min"),s=i.find(".tooltip-max")}),it("should have `tooltip-min, tooltip-max` displayed with `in` class",function(){expect(o.css("display")).not.toBe("none"),expect(o.hasClass("in")).toBeTruthy(),expect(s.css("display")).not.toBe("none"),expect(s.hasClass("in")).toBeTruthy()}),it("should have `tooltip-main` not displayed",function(){expect(e.css("display")).toBe("none"),expect(e.hasClass("in")).toBeFalsy()})}),afterEach(function(){t&&(t.slider("destroy"),t=null)})});